svg.script:
var shiftX = 25;
var shiftY = 40;
var radius = 24;
var height = 16;
var width = 3;
var colorA = "#8E56BA";
var colorB = "#343a40";
alpha = 90;

function DrawC(id, centerX, centerY, radius, alpha, stroke, width) {
    function dx() {
        return radius * Math.cos(alpha / 2 * Math.PI / 180.0)
    };
    function dy() {
        return radius * Math.sin(alpha / 2 * Math.PI / 180.0)
    };

    d = [
        "M", centerX + dx(), centerY - dy(),
        "a", radius, radius, 0, 1, 0, 0, dy() * 2
    ]

    document.getElementById(id).setAttribute('d', d.join(" "))
    document.getElementById(id).setAttribute('stroke-width', width)
    document.getElementById(id).setAttribute('stroke', stroke)
};

function DrawW(id, centerX, centerY, centerHeight, radius, stroke, width) {
    function xmax() {
        return Math.sqrt(radius ** 2 - centerHeight ** 2)
    };

    d = [
        "M", centerX - xmax(), centerY - centerHeight,
        "L",
        centerX - (xmax() / 2), centerY + centerHeight,
        centerX, centerY - (centerHeight * 0.25),
        centerX + (xmax() / 2), centerY + centerHeight,
        centerX + xmax(), centerY - centerHeight,
    ]

    document.getElementById(id).setAttribute('d', d.join(" "))
    document.getElementById(id).setAttribute('stroke-width', width)
    document.getElementById(id).setAttribute('stroke', stroke)
}

function DrawCircle(id, centerX , centerY, radius, fill, stroke, width) {
    circle = document.getElementById(id)
    circle.setAttribute('cx', centerX)
    circle.setAttribute('cy', centerY)
    if (fill == 'none') {
        circle.setAttribute('r', radius - (width / 2))
        circle.setAttribute('fill-opacity', 0)
    } else {
        circle.setAttribute('r', radius)
        circle.setAttribute('fill', fill)
    }
    circle.setAttribute('stroke', stroke)
    circle.setAttribute('stroke-width', width)
}

DrawCircle(1, shiftX, shiftY, radius, 'none', colorA, width);
DrawCircle(2, shiftX * 3, shiftY, radius, colorA, 'none');
DrawC('c', shiftX * 3, shiftY, height, alpha, colorB, width);
DrawW('w', shiftX, shiftY, height, radius, colorB, width);
